package leetcode.od3.window;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * 438. 找到字符串中所有字母异位词
 *
 * 给定两个字符串 s 和 p，找到 s 中所有 p 的 异位词 的子串，返回这些子串的起始索引。
 * 不考虑答案输出的顺序。
 * 异位词 指由相同字母重排列形成的字符串（包括相同的字符串）。
 *
 * 输入: s = "cbaebabacd", p = "abc"
 * 输出: [0,6]
 * 解释:
 * 起始索引等于 0 的子串是 "cba", 它是 "abc" 的异位词。
 * 起始索引等于 6 的子串是 "bac", 它是 "abc" 的异位词。
 */
public class Leetcode438 {

    public static void main(String[] args) {

        String s = "";
        String p = "";

        Leetcode438 yb = new Leetcode438();
        //yb.findAnagrams_("abacbabc","abc");
        yb.findAnagrams_("vwwvv","vwv");
    }

    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> res = new ArrayList<>();
        char[] arr = p.toCharArray();
        Arrays.sort(arr);
        String str = new String(arr);
        for(int i=0;i+p.length()<=s.length();i++){
            char[] temp = s.substring(i,i+p.length()).toCharArray();
            Arrays.sort(temp);
            if(str.equals(new String(temp))){
                res.add(i);
            }
        }
        return res;
    }


    public List<Integer> findAnagrams_(String s, String p) {
        List<Integer> res = new ArrayList<>();
        Map<Character,Integer> map = new HashMap<>();
        for(int i=0;i<p.length();i++){
            Character key = p.charAt(i);
            map.put(key,map.getOrDefault(key,0)+1);
        }

        Map<Character,Integer> matcher = new HashMap<>();
        int left = 0;
        int right = 0;
        int ver = 0;
        while(right<s.length()){
            Character key = s.charAt(right);
            if(map.containsKey(key)){
                matcher.put(key,matcher.getOrDefault(key,0)+1);
                if(map.get(key).equals(matcher.get(key))){
                    ver ++;
                    if(ver==map.size()){
                        res.add(left);
                    }
                }else if(matcher.get(key)>map.get(key)){
                    while(s.charAt(left) !=key) {
                        char cl = s.charAt(left);
                        if (matcher.get(cl).equals(map.get(cl))){
                            ver--;
                        }
                        matcher.put(cl,matcher.get(cl)-1);
                        left++;

                    }
                    matcher.put(key,matcher.get(key)-1);
                    left++;
                    if(ver==map.size()){
                        res.add(left);
                    }
                }
                right++;
            }else{
                right++;
                left=right;
                ver = 0;
                matcher.clear();
                continue;
            }

        }

        return res;
    }
}
